    constructor(videojocsService) {
        this.videojocsService = videojocsService;
        // Sacar la lista de juegos de AppComponent
        this.listaVideojocsBuscar = [];
    }
    listaFavoritos() {
        const favoritos = localStorage.getItem('favoritos');
        if (favoritos) {
            // Comprobar que el valor del localStorage tiene algo
            if (favoritos.length > 0) {
                return JSON.parse(favoritos);
            }
        }
        return [];
    }
    ngOnInit() {
        this.loadVideojocs();
    }
    // Recuperar la query desde sessionStorage
