import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { trigger, state, style, transition, animate } from '@angular/animations';
let AppComponent = class AppComponent {
    constructor(videojocsService) {
        this.videojocsService = videojocsService;
        this.listaVideojocs = [];
        this.searchValue = 'ride';
        this.isActive = false;
    }
    ngOnInit() {
        this.loadVideojocs();
    }
    loadVideojocs() {
        this.videojocsService.listarVideojocs().then((videojocs) => {
            this.listaVideojocs = videojocs;
        });
    }
    async searchResults(query) {
        const videojocs = await this.videojocsService.getSearchResults(query);
        this.listaVideojocs = videojocs.results;
        console.log(videojocs);
        console.log(this.listaVideojocs);
        console.log(this.searchValue);
        console.log(query);
    }
    toggle() {
        this.isActive = !this.isActive;
    }
};
AppComponent = __decorate([
    Component({
        selector: 'app-root',
        templateUrl: './app.component.html',
        styleUrls: ['./app.component.css'],
        animations: [
            // Animar boton del menu
            trigger('botonMenu', [
                state('void', style({
                    opacity: 0,
                    transform: 'translateX(-100%)'
                })),
                transition('void => *', animate('1000ms ease-in')),
                transition('* => void', animate('1000ms ease-out'))
            ]),
            // Al clicar el boton del menu, girar el boton
            trigger('botonMenu2', [
                state('active', style({
                    transform: 'rotate(45deg)'
                })),
                state('inactive', style({
                    transform: 'rotate(0deg)'
                })),
                transition('active => inactive', animate('500ms ease-out')),
                transition('inactive => active', animate('500ms ease-in'))
            ]),
            // Animar el boton de favoritos
        ]
    })
], AppComponent);
export { AppComponent };
